mapper.update:
update article
set title = #{title,jdbcType=VARCHAR},
  author = #{author,jdbcType=VARCHAR},
  time = #{time,jdbcType=TIMESTAMP},
  text = #{text,jdbcType=LONGVARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, title, author, time, text
from article
where id = #{id,jdbcType=INTEGER}
