mapper.select:
select * from article


    title LIKE  '%${title}%'


    and
    author = #{author}


    and
    state = #{state}


    and
    time between #{startTime} and #{endTime}


limit #{page},#{rows}

select count(1) from article


    title LIKE  '%${title}%'


    and
    author = #{author}


    and
    state = #{state}


    and
    time between #{startTime} and #{endTime}

mapper.delete:
delete from article
where id in

  #{item}

mapper.update:
update article
set state = #{state}
where id = #{id}
