mapper.insert:
insert into commission (commission_id, membership_grade, commission_ratio
  )
values (#{commissionId,jdbcType=INTEGER}, #{membershipGrade,jdbcType=VARCHAR}, #{commissionRatio,jdbcType=DOUBLE}
  )

mapper.update:
update commission
set membership_grade = #{membershipGrade,jdbcType=VARCHAR},
  commission_ratio = #{commissionRatio,jdbcType=DOUBLE}
where commission_id = #{commissionId,jdbcType=INTEGER}

mapper.select:
select commission_id, membership_grade, commission_ratio
from commission
where commission_id = #{commissionId,jdbcType=INTEGER}
