mapper.select:
select * from lecture


    name LIKE  '%${name}%'


    and
    status = #{status}


    and
    register_time between #{startTime} and #{endTime}


limit #{page},#{rows}

select count(1) from lecture


    name LIKE  '%${name}%'


    and
    status = #{status}


    and
    register_time between #{startTime} and #{endTime}

mapper.delete:
delete from lecture
where id in

  #{item}

mapper.update:
update lecture
set status = #{status}
where id = #{id}
