mapper.update:
update lecture_orders
set lectureid = #{lectureid,jdbcType=INTEGER},
  user_id = #{userId,jdbcType=INTEGER},
  commissionid = #{commissionid,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, lectureid, user_id, commissionid
from lecture_orders
where id = #{id,jdbcType=INTEGER}
