mapper.insert:
insert into native_places (native_id, native_province, native_city
  )
values (#{nativeId,jdbcType=INTEGER}, #{nativeProvince,jdbcType=VARCHAR}, #{nativeCity,jdbcType=VARCHAR}
  )

mapper.update:
update native_places
set native_province = #{nativeProvince,jdbcType=VARCHAR},
  native_city = #{nativeCity,jdbcType=VARCHAR}
where native_id = #{nativeId,jdbcType=INTEGER}

mapper.select:
select native_id, native_province, native_city
from native_places
where native_id = #{nativeId,jdbcType=INTEGER}
