mapper.update:
update service_classification
set icon = #{icon,jdbcType=VARCHAR},
  service_name = #{serviceName,jdbcType=VARCHAR},
  service_introduced = #{serviceIntroduced,jdbcType=VARCHAR}
where icon_id = #{iconId,jdbcType=INTEGER}

mapper.select:
select icon_id, icon, service_name, service_introduced
from service_classification
where icon_id = #{iconId,jdbcType=INTEGER}
